import React, { useState } from 'react';
import MapView from './map/mapView.jsx';
import ControlPanel from './Panels/ControlPanel.jsx'; 

function App() {
    // App bileşeni, canlı İHA konumlarını ve görev kayıtlarını tutar.
    const [liveUAVPositions, setLiveUAVPositions] = useState([]);
    const [taskRecords, setTaskRecords] = useState([]); 

    // MapView'den gelen canlı konum verisini App bileşenine almak için bir callback fonksiyonu
    const handleUAVUpdate = (positions) => {
        setLiveUAVPositions(positions);
    };

    // Fonksiyonun içinde sadece tek bir return ifadesi olmalıdır.
    return (

        <div className="App" style={{ display: 'flex', height: '100vh', width: '100vw' }}>

            {/* 1. KONTROL PANELİ (Soldaki Bar)*/}
            <div 
                style={{ 
                    width: '300px', 
                    backgroundColor:'rgba(144, 129, 129, 0.5)',
                    padding: '20px', 
                    overflowY: 'auto',
                    color: '#ecf0f1', 
                    boxShadow: '4px 0 10px rgba(234, 234, 234, 0.5)', 
                    zIndex: 100 
                }}
            >
                {}
                <h1 
                    style={{ 
                        borderBottom: '2px solid rgb(0, 0, 0)', 
                        paddingBottom: '15px', 
                        marginBottom: '25px', 
                        color: 'black',
                        fontSize: '35px' 
                    }}
                >
                    Hakem Sunucusu Klonu
                </h1>
                <p style={{ fontSize: '25px', color: '#e74c3c' }}>Sunucu: http://localhost:5000</p>

                <ControlPanel 
                    uavPositions={liveUAVPositions} 
                    locksAndKamikazes={taskRecords}
                />
            </div>

            {/* 2. HARİTA (Sağdaki Büyük Kısım) */}
            <div style={{ flexGrow: 1 }}>
                <MapView onUAVUpdate={handleUAVUpdate} />
            </div>
        </div>
    );
}

export default App;
