import React from 'react';

const ControlPanel = ({ uavPositions, locksAndKamikazes }) => {
    // Toplam İHA sayısını bulalım
    const activeUAVCount = uavPositions.length;

    return (
        <div style={{ padding: '10px' }}>
            {/* 1. Canlı Özet */}
            <h3>Canlı Telemetri Özeti ({activeUAVCount} İHA)</h3>
            <ul style={{ listStyle: 'none', padding: 0, fontSize: '14px' }}>
                {uavPositions.map(uav => (
                    <li key={uav.num} style={{ marginBottom: '5px', borderBottom: '1px solid #ccc' }}>
                        <strong>Takım {uav.num}</strong>: 
                        Enlem: {uav.lat.toFixed(5)}, Boylam: {uav.lng.toFixed(5)}<br/>
                        İrtifa: {uav.irtifa}m
                    </li>
                ))}
                {activeUAVCount === 0 && <li>Henüz telemetri verisi alınmadı.</li>}
            </ul>

            {/* 2. Görev Kayıtları */}
            <h3 style={{ marginTop: '20px' }}>Kilitlenme & Kamikaze Kayıtları</h3>
            {/* Not: Gerçekte bu veriler Server'dan GET/fetch ile çekilmelidir. 
                 Şimdilik sadece simüle bir liste göstereceğiz. */}
            <p style={{ fontSize: '14px', color: '#666' }}>
                *Kayıtlar: 0* (Server'dan çekilmelidir.)
            </p>
            <p style={{ fontSize: '12px', color: 'red' }}>
                Server'ı restart etmeyi unutmayın: `dotnet run`
            </p>
        </div>
    );
};

export default ControlPanel;
