import React from 'react';

// STİL: Başlıklar ve Listeler için koyu temaya uyum sağlayacak stil eklendi
const ControlPanel = ({ uavPositions, locksAndKamikazes }) => {
    const activeUAVCount = uavPositions.length;

    return (
        <div style={{ padding: '10px' }}>
            {/* 1. Canlı Özet */}
            <h2 style={{ borderBottom: '1px solid #34495e', paddingBottom: '5px', color: 'black' }}>
                Canlı Telemetri Özeti ({activeUAVCount} İHA)
            </h2>
            <ul style={{ listStyle: 'none', padding: 0, fontSize: '14px' }}>
                {uavPositions.map(uav => (
                    <li 
                      key={uav.num} 
                      style={{ 
                        marginBottom: '10px', 
                        padding: '8px', 
                        backgroundColor: '#34495e', // Liste elemanı arka planı
                        borderRadius: '4px' 
                      }}
                    >
                        <strong style={{ color: 'black' }}>Takım {uav.num}</strong>: 
                        <br/>
                        Enlem/Boylam: <span style={{ color: '#ecf0f1' }}>{uav.lat.toFixed(5)}, {uav.lng.toFixed(5)}</span><br/>
                        İrtifa: <span style={{ color: '#e74c3c' }}>{uav.irtifa}m</span>
                    </li>
                ))}
                {activeUAVCount === 0 && <li style={{ color: '#e74c3c', marginTop: '20px' }}>Henüz telemetri verisi alınmadı.</li>}
            </ul>

            {/* 2. Görev Kayıtları */}
            <h2 style={{ marginTop: '20px', borderBottom: '1px solid #34495e', paddingBottom: '5px', color: 'black' }}>
                Kilitlenme & Kamikaze Kayıtları
            </h2>
            <p style={{ fontSize: '20px', color: 'black' }}>
                *Kayıtlar: 0* (Server'dan çekilmelidir.)
            </p>
            <p style={{ fontSize: '20px', color: 'black' }}>
                Server'ı restart etmeyi unutmayın: `dotnet run`
            </p>
        </div>
    );
};

export default ControlPanel;
