import React, { useState } from 'react';
import MapView from './map/mapView.jsx'; // mapView.jsx olarak küçük harfle import et
import ControlPanel from './Panels/ControlPanel.jsx'; // Yeni bileşenimizi içeri aktar

function App() {
    // App bileşeni, canlı İHA konumlarını ve görev kayıtlarını tutar.
    const [liveUAVPositions, setLiveUAVPositions] = useState([]);
    const [taskRecords, setTaskRecords] = useState([]); // Kilitlenme/Kamikaze kayıtları

    // MapView'den gelen canlı konum verisini App bileşenine almak için bir callback fonksiyonu
    const handleUAVUpdate = (positions) => {
        // Bu fonksiyon, MapView her güncellendiğinde çağrılır.
        setLiveUAVPositions(positions);
    };

    return (
        <div className="App" style={{ display: 'flex', height: '100vh', width: '100vw' }}>
            {/* 1. KONTROL PANELİ (Soldaki Bar) */}
            <div style={{ width: '30%', backgroundColor: '#f0f0f0', padding: '20px', overflowY: 'auto' }}>
                <h2>Hakem Sunucusu Klonu</h2>
                <p>Sunucu: http://localhost:5000</p>

                {/* Canlı konum verisini ControlPanel'e iletiyoruz */}
                <ControlPanel 
                    uavPositions={liveUAVPositions} 
                    locksAndKamikazes={taskRecords}
                />
            </div>

            {/* 2. HARİTA (Sağdaki Büyük Kısım) */}
            <div style={{ flexGrow: 1 }}>
                {/* Haritaya, konum güncellendiğinde çağıracağı callback'i iletiyoruz */}
                <MapView onUAVUpdate={handleUAVUpdate} />
            </div>
        </div>
    );
}

export default App;
